import React, {useRef} from 'react'
import '../css/PausedPage.css'

const GameOver = (props) => {

    const hsRef = useRef('')
    const newGame = () => {
        props.newGameCalled()
    }

    const showHighScore = () => {
        let displayHS = ""
        if("highscore" in localStorage) {
            const highScoreData = JSON.parse(localStorage.getItem("highscore"))
            displayHS += '<ol class="hs-list">'
            highScoreData.forEach(element => {
                displayHS += '<li>'+ element.name +' - Score '+ element.score +'</li>'
            });
            displayHS += '</ol>'
        } else {
            displayHS = '<p>No High Score Found!</p>';
        }
        hsRef.current = displayHS
    }

    return (
        <section>
            <div className='pause__card'>
                <h3>GAME OVER</h3>
                <h4>Your Score: {props.playerScore}</h4>
                <div className='actions'>
                    <button onClick={showHighScore}>High Score</button>
                    <button onClick={newGame}>Main Menu</button>
                </div>
            </div>
            <div class="pause-card">
                <h1>High Score</h1>
                <div id="highscore-data" ref={hsRef}></div>
                <button onclick="window.location.reload()">Back</button>
            </div>
        </section>
    )
}

export default GameOver;
