import React, {useState} from 'react'
import '../css/GamePage.css'
import $ from 'jquery'

const GamePage = () => {

    const livesRemaining = () => {
        let lifeIndicator = []
        for (let index = 0; index < 5; index++) {
           lifeIndicator.push(<i className='fas fa-heart score-area__heart'></i>)
        }
        return lifeIndicator
    }

    const carrotFlow1 = (steps) => {
        let carrot = true
        let timing = Math.floor((Math.random() * 100) + 20)
        setInterval(function() {
            // if(!carrot) {
            //     document.getElementById('pos1').innerHTML = "<img className='carrots' id='carrot1' src='/bomb.png' alt='carrot'/>"
            // } else {
            //     document.getElementById('pos1').innerHTML = "<img className='carrots' id='carrot1' src='/carrot.png' alt='carrot'/>"
            // }
            document.getElementById('carrot1').style.top = steps + 'px';
            $('#carrot1').show()
            // document.getElementById('carrot1').style.transition = 'top 0.5s';
            if(steps < 500 ) {
               steps+= timing
            } else {
                steps=65
                $('#carrot1').hide()
                timing = Math.floor((Math.random() * 100) + 20)
                if(Math.floor((Math.random() * 10) + 1) === 9) {
                    carrot = false
                }
            }
        },Math.floor((Math.random() * 1000) + 100))
    }

    const carrotFlow2 = (steps) => {
        let timing = Math.floor((Math.random() * 100) + 20)
        setInterval(function() {
            document.getElementById('carrot2').style.top = steps + 'px';
            $('#carrot2').show()
            // document.getElementById('carrot2').style.transition = 'top .5s';
            if(steps < 500 ) {
                steps+= timing
            } else {
                steps=65
                $('#carrot2').hide()
                timing = Math.floor((Math.random() * 100) + 20)
            }
        },Math.floor((Math.random() * 1000) + 100))
    }

    const carrotFlow3 = (steps) => {
        let timing = Math.floor((Math.random() * 100) + 20)
        setInterval(function() {
            document.getElementById('carrot3').style.top = steps + 'px';
            $('#carrot3').show()
            // document.getElementById('carrot3').style.transition = 'top .5s';
            if(steps < 500 ) {
                steps+= timing
            } else {
                steps=65
                $('#carrot3').hide()
                timing = Math.floor((Math.random() * 100) + 20)
            }
        },Math.floor((Math.random() * 1000) + 100))
    }

    const carrotFlow4 = (steps) => {
        let timing = Math.floor((Math.random() * 100) + 20)
        setInterval(function() {
            document.getElementById('carrot4').style.top = steps + 'px';
            $('#carrot4').show()
            // document.getElementById('carrot4').style.transition = 'top 1s';
            if(steps < 500 ) {
                steps+= timing
            } else {
                steps=65
                $('#carrot4').hide() 
                timing = Math.floor((Math.random() * 100) + 20)
            }
        },Math.floor((Math.random() * 1000) + 100))
    }

    const carrotFlow5 = (steps) => {
        let timing = Math.floor((Math.random() * 100) + 20)
        setInterval(function() {
            document.getElementById('carrot5').style.top = steps + 'px';
            $('#carrot5').show()
            // document.getElementById('carrot5').style.transition = 'top 1s';
            if(steps < 500 ) {
                steps+= timing
            } else {
                steps=65
                $('#carrot5').hide()
                timing = Math.floor((Math.random() * 100) + 20)
            }
        },Math.floor((Math.random() * 1000) + 100))
    }

    const carrotFlow6 = (steps) => {
        let timing = Math.floor((Math.random() * 100) + 20)
        setInterval(function() {
            document.getElementById('carrot6').style.top = steps + 'px';
            $('#carrot6').show()
            // document.getElementById('carrot6').style.transition = 'top 1s';
            if(steps < 500 ) {
                steps+= timing
            } else {
                steps=65
                $('#carrot6').hide()
                timing = Math.floor((Math.random() * 100) + 20)
            }
        },Math.floor((Math.random() * 1000) + 100))
    }

    

    const startGame = () => {
        $('.btn').hide()

        carrotFlow1(65)
        carrotFlow2(65)
        carrotFlow3(65)
        carrotFlow4(65)
        carrotFlow5(65)
        carrotFlow6(65)
    }


    return (
        <div>
            <div className='score-area'>
                {livesRemaining()}
                <h4 className='score-area__score'>Score :</h4>
            </div>
            <div className='game-area'>
                <div id='pos1'><img className='carrots' id='carrot1' src="/bomb.png" alt='carrot'/></div>
                <div id='pos2'><img className='carrots' id='carrot2' src="/carrot.png" alt='carrot'/></div>
                <div id='pos3'><img className='carrots' id='carrot3' src="/carrot.png" alt='carrot'/></div>
                <div id='pos4'><img className='carrots' id='carrot4' src="/carrot.png" alt='carrot'/></div>
                <div id='pos5'><img className='carrots' id='carrot5' src="/carrot.png" alt='carrot'/></div>
                <div id='pos6'><img className='carrots' id='carrot6' src="/carrot.png" alt='carrot'/></div>
            </div>
                <button className='btn' onClick={startGame}>START</button>
            <div>
            </div>
        </div>
    )
}

export default GamePage;
