import React, {useState} from 'react'
import '../css/GamePage.css'
import $ from 'jquery'

const GamePage = () => {

    const livesRemaining = () => {
        let lifeIndicator = []
        for (let index = 0; index < 5; index++) {
           lifeIndicator.push(<i className='fas fa-heart score-area__heart'></i>)
        }
        return lifeIndicator
    }

    const check = () => {
        $('.btn').hide();
        $("#firstHi").animate({
            height: '+=50px',
            width: '+=50px'
        })
    }


    return (
        <div>
            <div className='score-area'>
                {livesRemaining()}
                <h4 className='score-area__score'>Score :</h4>
            </div>
            <div className='game-area'>
                <div className='game-area__itemfall'>
                    <img src="../images/carrot.png" alt='carrot'/>
                </div>
            </div>
                <button className='btn' onClick={check}>START</button>
            <div>
            </div>
        </div>
    )
}

export default GamePage;
