import React, {useState} from 'react'
import '../css/GamePage.css'
import $ from 'jquery'

const GamePage = () => {

    const timings = [35,40,45,50,55,60,65,70,80,90]
    let steps = [65,65,65,65,65,65]
    const [score, setScore] = useState(0)
    let scoreUpdate = 0
    const [lives,setLives] = useState(5)
    let carrots = [Math.floor((Math.random() * 10) + 1),Math.floor((Math.random() * 10) + 1),
        Math.floor((Math.random() * 10) + 1),Math.floor((Math.random() * 10) + 1),
        Math.floor((Math.random() * 10) + 1),Math.floor((Math.random() * 10) + 1)]
    let timing = [timings[Math.floor(Math.random() * 10)],timings[Math.floor(Math.random() * 10)],
    timings[Math.floor(Math.random() * 10)],timings[Math.floor(Math.random() * 10)],
    timings[Math.floor(Math.random() * 10)],timings[Math.floor(Math.random() * 10)]]

    const livesRemaining = (noOfLives) => {
        let lifeIndicator = []
        for (let index = 0; index < noOfLives; index++) {
           lifeIndicator.push(<i className='fas fa-heart score-area__heart'></i>)
        }
        return lifeIndicator
    }

    const carrotFlow1 = () => {
        if(carrots[0] === 9 || carrots[0] === 5) {
            document.getElementById('carrot1').src = '/bomb.png';
        } else if (carrots[0] === 7) {
            document.getElementById('carrot1').src = '/carrots.png';
        } else {
            document.getElementById('carrot1').src = '/carrot.png';
        }
        document.getElementById('carrot1').style.top = steps[0] + 'px';
        $('#carrot1').show()
        if(steps[0] < 450 ) {
            steps[0]+= timing[0]
        } else {
            checkFeed('#carrot1',document.getElementById('carrot1').src)
            steps[0]=65
            $('#carrot1').hide()
            carrots[0] = Math.floor((Math.random() * 10) + 1)
            timing[0] = timings[Math.floor(Math.random() * 10)]
        }
    }

    const carrotFlow2 = () => {
        if(carrots[1] === 9 || carrots[1] === 5) {
            document.getElementById('carrot2').src = '/bomb.png';
        }else if (carrots[1] === 7) {
            document.getElementById('carrot2').src = '/carrots.png';
        } else {
            document.getElementById('carrot2').src = '/carrot.png';
        }
        document.getElementById('carrot2').style.top = steps[1] + 'px';
        $('#carrot2').show()
        if(steps[1] < 450 ) {
            steps[1]+= timing[1]
        } else {
            checkFeed('#carrot2',document.getElementById('carrot2').src)
            steps[1]=65
            $('#carrot2').hide()
            carrots[1] = Math.floor((Math.random() * 10) + 1)
            timing[1] = timings[Math.floor(Math.random() * 10)]
        }
    }

    const carrotFlow3 = () => {
        if(carrots[2] === 9 || carrots[2] === 5) {
            document.getElementById('carrot3').src = '/bomb.png';
        } else if (carrots[2] === 7) {
            document.getElementById('carrot3').src = '/carrots.png';
        } else {
            document.getElementById('carrot3').src = '/carrot.png';
        }
        document.getElementById('carrot3').style.top = steps[2] + 'px';
        $('#carrot3').show()
        if(steps[2] < 450 ) {
            steps[2]+= timing[2]
        } else {
            checkFeed('#carrot3',document.getElementById('carrot3').src)
            steps[2]=65
            $('#carrot3').hide()
            carrots[2] = Math.floor((Math.random() * 10) + 1)
            timing[2] = timings[Math.floor(Math.random() * 10)]
        }
    }

    const carrotFlow4 = () => {
        if(carrots[3] === 9 || carrots[3] === 5) {
            document.getElementById('carrot4').src = '/bomb.png';
        } else if (carrots[3] === 7) {
            document.getElementById('carrot4').src = '/carrots.png';
        }else {
            document.getElementById('carrot4').src = '/carrot.png';
        }
        document.getElementById('carrot4').style.top = steps[3] + 'px';
        $('#carrot4').show()
        if(steps[3] < 450 ) {
            steps[3]+= timing[3]
        } else {
            checkFeed('#carrot4',document.getElementById('carrot4').src)
            steps[3]=65
            $('#carrot4').hide()
            carrots[3] = Math.floor((Math.random() * 10) + 1)
            timing[3] = timings[Math.floor(Math.random() * 10)]
        }
    }

    const carrotFlow5 = () => {
        if(carrots[4] === 9 || carrots[4] === 5) {
            document.getElementById('carrot5').src = '/bomb.png';
        } else if (carrots[4] === 7) {
            document.getElementById('carrot5').src = '/carrots.png';
        }else {
            document.getElementById('carrot5').src = '/carrot.png';
        }
        document.getElementById('carrot5').style.top = steps[4] + 'px';
        $('#carrot5').show()
        if(steps[4] < 450 ) {
            steps[4]+= timing[4]
        } else {
            checkFeed('#carrot5',document.getElementById('carrot5').src)
            steps[4]=65
            $('#carrot5').hide()
            carrots[4] = Math.floor((Math.random() * 10) + 1)
            timing[4] = timings[Math.floor(Math.random() * 10)]
        }
    }

    const carrotFlow6 = () => {
        if(carrots[5] === 9 || carrots[5] === 5) {
            document.getElementById('carrot6').src = '/bomb.png';
        } else if (carrots[5] === 7) {
            document.getElementById('carrot6').src = '/carrots.png';
        }else {
            document.getElementById('carrot6').src = '/carrot.png';
        }
        document.getElementById('carrot6').style.top = steps[5] + 'px';
        $('#carrot6').show()
        if(steps[5] < 450 ) {
            steps[5]+= timing[5]
        } else {
            checkFeed('#carrot6',document.getElementById('carrot6').src)
            steps[5]=65
            $('#carrot6').hide()
            carrots[5] = Math.floor((Math.random() * 10) + 1)
            timing[5] = timings[Math.floor(Math.random() * 10)]
        }
    }

    const clearFlow = () => {
        clearInterval(carrotFlow1)
        clearInterval(carrotFlow2)
        clearInterval(carrotFlow3)
        clearInterval(carrotFlow4)
        clearInterval(carrotFlow5)
        clearInterval(carrotFlow6)
        setLives(lives - 1)
        if(lives != 1) {
            startGame()
        } else {
            alert('Game Over.! Your score is ', score)
        }
    }

    const checkFeed = (carrotId,source) => {
        if($('.rabbit').css('left') === $(carrotId).css('margin-left') && source.includes('/carrot.png')) {
            scoreUpdate+=10
            setScore(scoreUpdate)
        } else if ($('.rabbit').css('left') === $(carrotId).css('margin-left') && source.includes('/bomb.png')) {
            $('.rabbit').hide()
            $('.boom').css('left', $('.rabbit').css('left'))
            $('.boom').show()
            clearFlow()
        }
    } 

    const startGame = () => {
        $('.boom').hide()
        $('.btn').hide();
        $('.rabbit').css('display', 'inline-flex')
        setInterval(carrotFlow1 ,1000)
        setInterval(carrotFlow2 ,Math.floor((Math.random() * 1000) + 200))
        setInterval(carrotFlow3 ,Math.floor((Math.random() * 1000) + 200))
        setInterval(carrotFlow4 ,Math.floor((Math.random() * 1000) + 200))
        setInterval(carrotFlow5 ,Math.floor((Math.random() * 1000) + 200))
        setInterval(carrotFlow6 ,Math.floor((Math.random() * 1000) + 200))
    }

    document.onkeydown = (event) => {
        if(event.key === 'ArrowLeft') {
            if($('.rabbit').css('left') != '0px') {
                $('.rabbit').css('left', '-=50px');
            }
        } else if (event.key === 'ArrowRight') {
            if($('.rabbit').css('left') != '1050px') {
                $('.rabbit').css('left', '+=50px');
            }
        }
    }

    return (
        <div id='all'>
            <div className='score-area'>
                {livesRemaining(lives)}
                <h4 className='score-area__score'>Score : {score}</h4>
            </div>
            <div className='game-area'>
                <div><img className='carrots' id='carrot1' src="/carrot.png" alt='carrot'/></div>
                <div><img className='carrots' id='carrot2' src="/carrot.png" alt='carrot'/></div>
                <div><img className='carrots' id='carrot3' src="/carrot.png" alt='carrot'/></div>
                <div><img className='carrots' id='carrot4' src="/carrot.png" alt='carrot'/></div>
                <div><img className='carrots' id='carrot5' src="/carrot.png" alt='carrot'/></div>
                <div><img className='carrots' id='carrot6' src="/carrot.png" alt='carrot'/></div>
            </div>
                <button className='btn' onClick={startGame}>START</button>
            <div>
                <div><img className='rabbit' id='bunny' src='/bunny.png' alt='bunny'/></div>
                <div><img className='boom' id='blast' src='/boom.png' alt='busted'/></div>
            </div>
        </div>
    )
}

export default GamePage;
