import React, {useRef, useEffect, useState} from 'react'
import '../css/GamePage.css'
import $ from 'jquery'
import PausedPage from './PausedPage'

const GamePage = () => {

    const timings = [35,40,45,50,55,60,65,70,80,90]
    const [score, setScore] = useState(0)
    let scoreUpdate = 0
    const [lives,setLives] = useState(5)
    let gameFreeze = false
    let newGameVariable = false

    const srcFirstCarrot = useRef('/carrot.png')
    const srcSecondCarrot = useRef('/carrot.png')
    const srcThirdCarrot = useRef('/carrot.png')
    const srcForthCarrot = useRef('/carrot.png')
    const srcFifthCarrot = useRef('/carrot.png')
    const srcSixthCarrot = useRef('/carrot.png')
    const pausedPageRef = useRef('')
    const gameSectionRef = useRef('')

    const livesRemaining = (noOfLives) => {
        let lifeIndicator = []
        for (let index = 0; index < noOfLives; index++) {
           lifeIndicator.push(<i className='fas fa-heart heart'></i>)
        }
        return lifeIndicator
    }

    const carrotFlow1 = (steps) => {
        let carrot = Math.floor((Math.random() * 10) + 1)
        let timing = timings[Math.floor(Math.random() * 10)]
        const flow = setInterval(function() {
            if(!gameFreeze) {
                if(carrot === 9 || carrot === 5) {
                    srcFirstCarrot.current.src = '/bomb.png';
                } else if (carrot === 7) {
                    srcFirstCarrot.current.src = '/carrots.png';
                }else {
                    srcFirstCarrot.current.src = '/carrot.png';
                }
                srcFirstCarrot.current.style.top = steps + 'px';
                $('#carrot1').show()
                if(steps < 450 ) {
                    steps+= timing
                } else {
                    checkFeed('#carrot1',srcFirstCarrot.current.src)
                    steps=65
                    $('#carrot1').hide()
                    carrot = Math.floor((Math.random() * 10) + 1)
                    timing = timings[Math.floor(Math.random() * 10)]
                }
        }
        if(newGameVariable) {
            clearInterval(flow)
        } },Math.floor((Math.random() * 1000) + 200))
    }

    const carrotFlow2 = (steps) => {
        let carrot = Math.floor((Math.random() * 10) + 1)
        let timing = timings[Math.floor(Math.random() * 10)]
        const flow = setInterval(function() {
            if(!gameFreeze) {
                if(carrot === 9 || carrot === 5) {
                    srcSecondCarrot.current.src =  '/bomb.png';
                } else if (carrot === 7) {
                    srcSecondCarrot.current.src = '/carrots.png';
                }else {
                    srcSecondCarrot.current.src = '/carrot.png';
                }
                srcSecondCarrot.current.style.top = steps + 'px';
                $('#carrot2').show()
                if(steps < 450 ) {
                    steps+= timing
                } else {
                    checkFeed('#carrot2',srcSecondCarrot.current.src)
                    steps=65
                    $('#carrot2').hide()
                    carrot = Math.floor((Math.random() * 10) + 1)
                    timing = timings[Math.floor(Math.random() * 10)]
                }
            }
            if(newGameVariable) {
                clearInterval(flow)
            } 
        },Math.floor((Math.random() * 1000) + 200))
    }

    const carrotFlow3 = (steps) => {
        let carrot = Math.floor((Math.random() * 10) + 1)
        let timing = timings[Math.floor(Math.random() * 10)]
        const flow = setInterval(function() {
            if(!gameFreeze) {
                if(carrot === 9 || carrot === 5) {
                    srcThirdCarrot.current.src =  '/bomb.png';
                } else if (carrot === 7) {
                    srcThirdCarrot.current.src = '/carrots.png';
                }else {
