                }
                srcThirdCarrot.current.style.top = steps + 'px';
                $('#carrot3').show()
                if(steps < 450 ) {
                    steps+= timing
                } else {
                    checkFeed('#carrot3', srcThirdCarrot.current.src)
                    steps=65
                    $('#carrot3').hide()
                    carrot = Math.floor((Math.random() * 10) + 1)
                    timing = timings[Math.floor(Math.random() * 10)]
                }
            }
            if(newGameVariable) {
                clearInterval(flow)
            }
        },Math.floor((Math.random() * 1000) + 200))
    }

