        let carrot = Math.floor((Math.random() * 10) + 1)
        let timing = timings[Math.floor(Math.random() * 10)]
        const flow = setInterval(function() {
            if(!gameFreeze) {
                if(carrot === 9 || carrot === 5) {
                    srcForthCarrot.current.src =  '/bomb.png';
                } else if (carrot === 7) {
                    srcForthCarrot.current.src = '/carrots.png';
                }else {
                    srcForthCarrot.current.src = '/carrot.png';
                }
