                    timing = timings[Math.floor(Math.random() * 10)]
                }
            }
            if(newGameVariable) {
                clearInterval(flow)
            }
        },Math.floor((Math.random() * 1000) + 200))
    }

    const carrotFlow5 = (steps) => {
        let carrot = Math.floor((Math.random() * 10) + 1)
        let timing = timings[Math.floor(Math.random() * 10)]
        const flow = setInterval(function() {
            if(!gameFreeze) {
                if(carrot === 9 || carrot === 5) {
                    srcFifthCarrot.current.src = '/bomb.png';
                } else if (carrot === 7) {
                    srcFifthCarrot.current.src = '/carrots.png';
                }else {
                    srcFifthCarrot.current.src = '/carrot.png';
                }
                srcFifthCarrot.current.style.top = steps + 'px';
                $('#carrot5').show()
                if(steps < 450 ) {
                    steps+= timing
                } else {
                    checkFeed('#carrot5', srcFifthCarrot.current.src)
                    steps=65
                    $('#carrot5').hide()
                    carrot = Math.floor((Math.random() * 10) + 1)
                    timing = timings[Math.floor(Math.random() * 10)]
                }
            }
            if(newGameVariable) {
                clearInterval(flow)
            }
        },Math.floor((Math.random() * 1000) + 200))
    }

    const carrotFlow6 = (steps) => {
        let carrot = Math.floor((Math.random() * 10) + 1)
        let timing = timings[Math.floor(Math.random() * 10)]
        const flow = setInterval(function() {
            if(!gameFreeze) {
                if(carrot === 9 || carrot === 5) {
                    srcSixthCarrot.current.src = '/bomb.png';
                } else if (carrot === 7) {
                    srcSixthCarrot.current.src = '/carrots.png';
                }else {
                    srcSixthCarrot.current.src = '/carrot.png';
                }
                srcSixthCarrot.current.style.top = steps + 'px';
