                clearInterval(flow)
            }
        },Math.floor((Math.random() * 1000) + 200))
    }

    const checkFeed = (carrotId,source) => {
        if($('.rabbit').css('left') === $(carrotId).css('margin-left') && source.includes('/carrot.png')) {
            scoreUpdate+=10
            setScore(scoreUpdate)
        } else if ($('.rabbit').css('left') === $(carrotId).css('margin-left') && source.includes('/bomb.png')) {
            $('.rabbit').hide()
            $('.boom').css('left', $('.rabbit').css('left'))
            $('.boom').show()
            setLives((prevState) => { return prevState-1})
            setTimeout(resumeGame, 3000)
        }
    }

    const resumeGame = () => {
        $('.boom').hide()
        $('.rabbit').css('left','450px')
        $('.rabbit').show()
    }

    const unPause = () => {
        const position = [50,250,450,650,850,1050]
        pausedPageRef.current.style.display = "none"
        gameSectionRef.current.style.opacity = 1
        $('.rabbit').css('left', position[Math.floor(Math.random() * 10)]+'px')
        $('.rabbit').css('display', 'inline-flex')
        gameFreeze = false
    }

    const pausePressed = () => {
        gameFreeze = true
        pausedPageRef.current.style.display = "block"
        gameSectionRef.current.style.opacity = 0.5
        $('.rabbit').css('display', 'none')
    }

    const newGame = () => {
        newGameVariable = true
        pausedPageRef.current.style.display = "none"
        gameSectionRef.current.style.opacity = 1
        // gameFreeze = false
        // setTimeout(startGame, 3000)
        setTimeout(carrotFlow1(65),3000)
    }

    const startGame = () => {
        newGameVariable = false
        $('.score-area').css('display', 'inline-flex')
        $('.carrots').css('display', 'inline-flex')
        $('.boom').hide()
        $('.btn').hide();
        carrotFlow1(65)
        carrotFlow2(65)
        carrotFlow3(65)
        carrotFlow4(65)
        carrotFlow5(65)
        carrotFlow6(65)
        $('.rabbit').css('display', 'inline-flex')
    }

    document.onkeydown = (event) => {
        if(event.key === 'ArrowLeft') {
            if($('.rabbit').css('left') !== '0px') {
                $('.rabbit').css('left', '-=50px');
            }
        } else if (event.key === 'ArrowRight') {
            if($('.rabbit').css('left') !== '1050px') {
                $('.rabbit').css('left', '+=50px');
            }
        }
    }

    return (
        <section>
            <div id='all'>
                <div className='score-area'>
                    <div className='score-area__score'><h4>Score : {score}</h4></div>
                    <div className='score-area__heart'>{livesRemaining(lives)}</div>
                    <i onClick= {(event) => pausePressed()} className="fas fa-pause fa-3x pauseBtn"></i>
                </div>
                <section id='game-section' ref={gameSectionRef}>
                    <div className='game-area'>
                        <div><img className='carrots' id='carrot1' src="/carrot.png" ref={srcFirstCarrot} alt='carrot'/></div>
                        <div><img className='carrots' id='carrot2' src="/carrot.png" ref={srcSecondCarrot} alt='carrot'/></div>
                        <div><img className='carrots' id='carrot3' src="/carrot.png" ref={srcThirdCarrot} alt='carrot'/></div>
                        <div><img className='carrots' id='carrot4' src="/carrot.png" ref={srcForthCarrot} alt='carrot'/></div>
                        <div><img className='carrots' id='carrot5' src="/carrot.png" ref={srcFifthCarrot} alt='carrot'/></div>
                        <div><img className='carrots' id='carrot6' src="/carrot.png" ref={srcSixthCarrot} alt='carrot'/></div>
                    </div>
                        <button className='btn' onClick={startGame}>START</button>
                    <div>
                        <div><img className='rabbit' id='bunny' src='/bunny.png' alt='bunny'/></div>
                        <div><img className='boom' id='blast' src='/boom.png' alt='busted'/></div>
                    </div>
                </section>
            </div>
            <div id='pausedPage' ref={pausedPageRef}>
                <PausedPage gameResume={unPause} newGameCalled={newGame}/>
            </div>
        </section>
    )
}

export default GamePage;
