import React, {useState} from 'react'
import '../css/GamePage.css'
import $ from 'jquery'
import PausedPage from './PausedPage'

const GamePage = () => {

    const timings = [35,40,45,50,55,60,65,70,80,90]
    const [score, setScore] = useState(1600)
    let scoreUpdate = 0
    const [lives,setLives] = useState(5)
    let gameFreeze = false

    let timing = [timings[Math.floor(Math.random() * 10)],timings[Math.floor(Math.random() * 10)],
    timings[Math.floor(Math.random() * 10)],timings[Math.floor(Math.random() * 10)],
    timings[Math.floor(Math.random() * 10)],timings[Math.floor(Math.random() * 10)]]

    let carrot = [Math.floor((Math.random() * 10) + 1),Math.floor((Math.random() * 10) + 1),
        Math.floor((Math.random() * 10) + 1),Math.floor((Math.random() * 10) + 1),
        Math.floor((Math.random() * 10) + 1),Math.floor((Math.random() * 10) + 1)]

    let steps = [65,65,65,65,65,65]

    const livesRemaining = (noOfLives) => {
        let lifeIndicator = []
        for (let index = 0; index < noOfLives; index++) {
           lifeIndicator.push(<i className='fas fa-heart heart'></i>)
        }
        return lifeIndicator
    }

    const carrotFlow1 = () => {
        if(!gameFreeze) {
            if(carrot[0] === 9 || carrot[0] === 5) {
                document.getElementById('carrot6').src = '/bomb.png';
            } else if (carrot[0] === 7) {
                document.getElementById('carrot6').src = '/carrots.png';
            }else {
                document.getElementById('carrot6').src = '/carrot.png';
            }
            document.getElementById('carrot6').style.top = steps[0] + 'px';
            $('#carrot6').show()
            if(steps[0] < 450 ) {
                steps[0]+= timing[0]
            } else {
                checkFeed('#carrot6',document.getElementById('carrot6').src)
                steps[0]=65
                $('#carrot6').hide()
                carrot[0] = Math.floor((Math.random() * 10) + 1)
                timing[0] = timings[Math.floor(Math.random() * 10)]
            }
        }
    }

    const carrotFlow2 = () => {
        if(!gameFreeze){
            if(carrot[1] === 9 || carrot[1] === 5) {
                document.getElementById('carrot6').src = '/bomb.png';
            } else if (carrot[1] === 7) {
                document.getElementById('carrot6').src = '/carrots.png';
            }else {
                document.getElementById('carrot6').src = '/carrot.png';
            }
            document.getElementById('carrot6').style.top = steps[1] + 'px';
            $('#carrot6').show()
            if(steps[1] < 450 ) {
                steps[1]+= timing[1]
            } else {
                checkFeed('#carrot6',document.getElementById('carrot6').src)
                steps[1]=65
                $('#carrot6').hide()
                carrot[1] = Math.floor((Math.random() * 10) + 1)
                timing[1] = timings[Math.floor(Math.random() * 10)]
            }
        }
    }

    const carrotFlow3 = () => {
        if(!gameFreeze){
            if(carrot[2] === 9 || carrot[2] === 5) {
                document.getElementById('carrot6').src = '/bomb.png';
            } else if (carrot[2] === 7) {
                document.getElementById('carrot6').src = '/carrots.png';
            }else {
                document.getElementById('carrot6').src = '/carrot.png';
            }
            document.getElementById('carrot6').style.top = steps[2] + 'px';
            $('#carrot6').show()
            if(steps[2] < 450 ) {
                steps[2]+= timing[2]
            } else {
                checkFeed('#carrot6',document.getElementById('carrot6').src)
                steps[2]=65
                $('#carrot6').hide()
                carrot[2] = Math.floor((Math.random() * 10) + 1)
                timing[2] = timings[Math.floor(Math.random() * 10)]
            }
        }
    }

    const carrotFlow4 = () => {
        if(!gameFreeze){
            if(carrot[3] === 9 || carrot[3] === 5) {
                document.getElementById('carrot6').src = '/bomb.png';
            } else if (carrot[3] === 7) {
                document.getElementById('carrot6').src = '/carrots.png';
            }else {
                document.getElementById('carrot6').src = '/carrot.png';
            }
            document.getElementById('carrot6').style.top = steps[3] + 'px';
            $('#carrot6').show()
            if(steps[3] < 450 ) {
                steps[3]+= timing[3]
            } else {
                checkFeed('#carrot6',document.getElementById('carrot6').src)
                steps[3]=65
                $('#carrot6').hide()
                carrot[3] = Math.floor((Math.random() * 10) + 1)
                timing[3] = timings[Math.floor(Math.random() * 10)]
            }
        }
    }

    function carrotFlow5() {
        if(!gameFreeze){
            if(carrot[4] === 9 || carrot[4] === 5) {
                document.getElementById('carrot6').src = '/bomb.png';
            } else if (carrot[4] === 7) {
                document.getElementById('carrot6').src = '/carrots.png';
            }else {
                document.getElementById('carrot6').src = '/carrot.png';
            }
            document.getElementById('carrot6').style.top = steps[4] + 'px';
            $('#carrot6').show()
            if(steps[4] < 450 ) {
                steps[4]+= timing[4]
            } else {
                checkFeed('#carrot6',document.getElementById('carrot6').src)
                steps[4]=65
                $('#carrot6').hide()
                carrot[4] = Math.floor((Math.random() * 10) + 1)
                timing[4] = timings[Math.floor(Math.random() * 10)]
            }
        }
    }

    function carrotFlow6() {
        if(!gameFreeze){
            if(carrot[5] === 9 || carrot[5] === 5) {
                document.getElementById('carrot6').src = '/bomb.png';
            } else if (carrot[5] === 7) {
                document.getElementById('carrot6').src = '/carrots.png';
            }else {
                document.getElementById('carrot6').src = '/carrot.png';
            }
            document.getElementById('carrot6').style.top = steps[5] + 'px';
            $('#carrot6').show()
            if(steps[5] < 450 ) {
                steps[5]+= timing[5]
            } else {
                checkFeed('#carrot6',document.getElementById('carrot6').src)
                steps[5]=65
                $('#carrot6').hide()
                carrot[5] = Math.floor((Math.random() * 10) + 1)
                timing[5] = timings[Math.floor(Math.random() * 10)]
            }
        }
    }

    const checkFeed = (carrotId,source) => {
        if($('.rabbit').css('left') === $(carrotId).css('margin-left') && source.includes('/carrot.png')) {
            scoreUpdate+=10
            setScore(scoreUpdate)
        } else if ($('.rabbit').css('left') === $(carrotId).css('margin-left') && source.includes('/bomb.png')) {
            $('.rabbit').hide()
            $('.boom').css('left', $('.rabbit').css('left'))
            $('.boom').show()
            setLives((prevState) => { return prevState-1})
            setTimeout(resumeGame, 3000)
        }
    }

    
    const firstCarrot = setInterval(carrotFlow1,Math.floor((Math.random() * 1000) + 200))
    const secondCarrot = setInterval(carrotFlow2,Math.floor((Math.random() * 1000) + 200))
    const thirdCarrot = setInterval(carrotFlow3,Math.floor((Math.random() * 1000) + 200))
    const forthCarrot = setInterval(carrotFlow4,Math.floor((Math.random() * 1000) + 200))
    const fifthCarrot = setInterval(carrotFlow5,Math.floor((Math.random() * 1000) + 200))
    const sixthCarrot = setInterval(carrotFlow6,Math.floor((Math.random() * 1000) + 200))

    const resumeGame = () => {
        $('.boom').hide()
        $('.rabbit').css('left','450px')
        $('.rabbit').show()
    }

    const pausedGame = () => {
        gameFreeze = false
        $('#game-section').toggle()
        $('#pausedPage').toggle()
    }

    const pausePressed = () => {
        gameFreeze = true
        $('#game-section').toggle()
        $('#pausedPage').toggle()
    }

    const startGame = () => {
        $('.boom').hide()
        $('.btn').hide();
        $('.rabbit').css('display', 'inline-flex')
    }

    document.onkeydown = (event) => {
        if(event.key === 'ArrowLeft') {
            if($('.rabbit').css('left') !== '0px') {
                $('.rabbit').css('left', '-=50px');
            }
        } else if (event.key === 'ArrowRight') {
            if($('.rabbit').css('left') !== '1050px') {
                $('.rabbit').css('left', '+=50px');
            }
        }
    }

    return (
        <section>
            <div id='all'>
                <div className='score-area'>
                    <div className='score-area__score'><h4>Score : {score}</h4></div>
                    <div className='score-area__heart'>{livesRemaining(lives)}</div>
                    <i onClick= {pausePressed} className="fas fa-pause fa-3x pauseBtn"></i>
                </div>
                <section id='game-section'>
                    <div className='game-area'>
                        <div><img className='carrots' id='carrot1' src="/carrot.png" alt='carrot'/></div>
                        <div><img className='carrots' id='carrot2' src="/carrot.png" alt='carrot'/></div>
                        <div><img className='carrots' id='carrot3' src="/carrot.png" alt='carrot'/></div>
                        <div><img className='carrots' id='carrot4' src="/carrot.png" alt='carrot'/></div>
                        <div><img className='carrots' id='carrot5' src="/carrot.png" alt='carrot'/></div>
                        <div><img className='carrots' id='carrot6' src="/carrot.png" alt='carrot'/></div>
                    </div>
                        <button className='btn' onClick={startGame}>START</button>
                    <div>
                        <div><img className='rabbit' id='bunny' src='/bunny.png' alt='bunny'/></div>
                        <div><img className='boom' id='blast' src='/boom.png' alt='busted'/></div>
                    </div>
                </section>
            </div>
            <div id='pausedPage'>
                <PausedPage gameResume={pausedGame}/>
            </div>
        </section>
    )
}

export default GamePage;
