import React, {useState} from 'react'
import '../css/GamePage.css'
import $ from 'jquery'

const GamePage = () => {

    let top = 0;

    const livesRemaining = () => {
        let lifeIndicator = []
        for (let index = 0; index < 5; index++) {
           lifeIndicator.push(<i className='fas fa-heart score-area__heart'></i>)
        }
        return lifeIndicator
    }

    setInterval(function() {
        document.getElementById('carrot').style.top = top + 'px';
        top+=20;
    }, 500)


    return (
        <div>
            <div className='score-area'>
                {livesRemaining()}
                <h4 className='score-area__score'>Score :</h4>
            </div>
            <div className='game-area'>
                <img id='carrot' src="/carrot.png" alt='carrot'/>
            </div>
                <button className='btn'>START</button>
            <div>
            </div>
        </div>
    )
}

export default GamePage;
