            if(document.getElementById('carrot2').style.top < 450 ) {
                document.getElementById('carrot2').style.top += timings[1]
            } else {
                checkFeed('#carrot2',document.getElementById('carrot2').src)
                document.getElementById('carrot2').style.top = 65 + 'px'
                $('#carrot2').hide()
            }
        }
    }

