import React, {useState} from 'react'
import '../css/GamePage.css'
import $ from 'jquery'

const GamePage = () => {

    const livesRemaining = () => {
        let lifeIndicator = []
        for (let index = 0; index < 5; index++) {
           lifeIndicator.push(<i className='fas fa-heart score-area__heart'></i>)
        }
        return lifeIndicator
    }

    const carrotFlow1 = (steps,timing) => {
        setInterval(function() {
            document.getElementById('carrot1').style.top = steps + 'px';
            document.getElementById('carrot1').style.transition = 'top 0.5s';
            if(steps < 500 ) {
               steps+= timing
            } else {
                steps=65
                $('#carrot1').hide()
                document.getElementById('carrot1').style.top = steps + 'px';
                $('#carrot1').unhide()
                // document.getElementById('carrot1').style.transition = 'top 0.5s';
            }
        },10000/(500/timing))
    }

    const carrotFlow2 = (steps,timing) => {
        setInterval(function() {
        document.getElementById('carrot2').style.top = steps + 'px';
        document.getElementById('carrot2').style.transition = 'top 1s';
        if(steps < 500 ) {
            steps+= timing
        } else {
            clearInterval(carrotFlow2)
        }
    },10000/(500/timing))
}

    const carrotFlow3 = (steps,timing) => {
        setInterval(function() {
        document.getElementById('carrot3').style.top = steps + 'px';
        document.getElementById('carrot3').style.transition = 'top 1s';
        if(steps < 500 ) {
            steps+= timing
        } else {
            clearInterval(carrotFlow3)
        }
    },10000/(500/timing))
}

    const carrotFlow4 = (steps,timing) => {
        setInterval(function() {
        document.getElementById('carrot4').style.top = steps + 'px';
        document.getElementById('carrot4').style.transition = 'top 1s';
        if(steps < 500 ) {
            steps+= timing
        } else {
            clearInterval(carrotFlow4)
        }
    },10000/(500/timing))
}

    const carrotFlow5 = (steps,timing) => {
        setInterval(function() {
        document.getElementById('carrot5').style.top = steps + 'px';
        document.getElementById('carrot5').style.transition = 'top 1s';
        if(steps < 500 ) {
            steps+= timing
        } else {
            clearInterval(carrotFlow5)
        }
    },10000/(500/timing))
}

    const carrotFlow6 = (steps,timing) => {
        setInterval(function() {
        document.getElementById('carrot6').style.top = steps + 'px';
        document.getElementById('carrot6').style.transition = 'top 1s';
        if(steps < 500 ) {
            steps+= timing
        } else {
            clearInterval(carrotFlow6)
        }
    },10000/(500/timing))
}

    

    const startGame = () => {
        $('.btn').hide()
        let car = [65,65,65,65,65,65]
        let timeVar = [Math.floor((Math.random() * 100) + 1),
        Math.floor((Math.random() * 100) + 10),
        Math.floor((Math.random() * 100) + 20),
        Math.floor((Math.random() * 100) + 5),
        Math.floor((Math.random() * 100) + 25),
        Math.floor((Math.random() * 100) + 30)]

        carrotFlow1(car[0],timeVar[0])
        carrotFlow2(car[1],timeVar[1])
        carrotFlow3(car[2],timeVar[2])
        carrotFlow4(car[3],timeVar[3])
        carrotFlow5(car[4],timeVar[4])
        carrotFlow6(car[5],timeVar[5])
    }


    return (
        <div>
            <div className='score-area'>
                {livesRemaining()}
                <h4 className='score-area__score'>Score :</h4>
            </div>
            <div className='game-area'>
                <div><img className='carrots' id='carrot1' src="/carrot.png" alt='carrot'/></div>
                <div><img className='carrots' id='carrot2' src="/carrot.png" alt='carrot'/></div>
                <div><img className='carrots' id='carrot3' src="/carrot.png" alt='carrot'/></div>
                <div><img className='carrots' id='carrot4' src="/carrot.png" alt='carrot'/></div>
                <div><img className='carrots' id='carrot5' src="/carrot.png" alt='carrot'/></div>
                <div><img className='carrots' id='carrot6' src="/carrot.png" alt='carrot'/></div>
            </div>
                <button className='btn' onClick={startGame}>START</button>
            <div>
            </div>
        </div>
    )
}

export default GamePage;
